    private void jtfResultadoActionPerformed(java.awt.event.ActionEvent evt) {                                             

        int valor1 = Integer.parseInt(jtfValor1.getText()); 
        int valor2 = Integer.parseInt(jtfValor2.getText());
        int soma = valor1 + valor2;
        jtfResutl.setText(String.valueOf(soma));
        jtfValor1.setText("");
        jtfValor2.setText("");
    }                                            

